jQuery(function() {
	initMobileNav();
	initOpenClose();
	initCustomForms();
	initRetinaCover();
	initFiltration();
	initSlickCarousel();
	initCustomPopup();
	initInViewport();
});

function initRetinaCover() {
	jQuery('.bg-stretch').retinaCover();
}


// filtration init
function initFiltration() {
	jQuery('.filter-wrap').each(function() {
		var holder = jQuery(this);
		var filtersSelect = holder.find('.select-holder select');
		var grid = holder.find('.filter-bio');
		var search = holder.find('.searh-holder [type="search"]');
		var filters = {};
		var qsRegex;

		// Isotope init
		grid.isotope({
			itemSelector: '.item',
			layoutMode: 'fitRows',
		});

		function changeValue() {
			grid.isotope({
				filter: function() {
					var $this = jQuery(this);
					var currentFilters = concatValues( filters );
					var searchResult = qsRegex ? $this.text().match(qsRegex) : true;
					var selectResult = currentFilters ? $this.is( currentFilters ) : true;

					return searchResult && selectResult;
				}
			});
		}

		function debounce( fn, threshold ) {
			var timeout;
			threshold = threshold || 100;
			return function debounced() {
				clearTimeout( timeout );
				var args = arguments;
				var _this = this;
				function delayed() {
					fn.apply( _this, args );
				}
				timeout = setTimeout( delayed, threshold );
			};
		}

		// flatten object by concatting values
		function concatValues( obj ) {
			var value = '';
			for ( var prop in obj ) {
				value += obj[ prop ];
			}
				return value;
		}

		// handlers
		filtersSelect.on( 'change', function() {
			var $this = $(this);
			var filterGroup = $this.attr('data-filter-group');

			filters[ filterGroup ] = $this.val();

			changeValue();
		});

		search.on('keyup', debounce( function() {
			qsRegex = new RegExp( "^\\s*\\b("+jQuery(this).val()+")", 'gmi' );
			changeValue();
		}, 200 ) );
	});
}

// slick init
function initSlickCarousel() {
	jQuery('.comment-slider').slick({
		slidesToScroll: 1,
		rows: 0,
		prevArrow: '<button class="slick-prev"><span class="icon-angle-left"></span></button>',
		nextArrow: '<button class="slick-next"><span class="icon-angle-right"></span></button>'
	});

	jQuery('.slick-slider-frame').slick({
		slidesToScroll: 1,
		rows: 0,
		slidesToShow: 3,
		prevArrow: '<button class="slick-prev"><span class="icon-angle-left"></span></button>',
		nextArrow: '<button class="slick-next"><span class="icon-angle-right"></span></button>',
		responsive: [{
			breakpoint: 992,
			settings: {
				slidesToScroll: 1,
				slidesToShow: 2
			}
		}, {
			breakpoint: 768,
			settings: {
				slidesToScroll: 1,
				slidesToShow: 1
			}
		}]
	});
}

// initialize custom form elements
function initCustomForms() {
	jcf.setOptions('Select', {
		wrapNative: false,
		wrapNativeOnMobile: false,
	});
	jcf.replaceAll();
}

// popups init
function initCustomPopup() {
	var body = jQuery('body');
	var className = "popup-open";
	
	jQuery('.popup-wrap').customPopup({
		btnOpen: '.open',
		btnClose: '.close',
		openClass: 'popup-active',
		hideOnClickOutside: false,
		onShow: function(self) {
			body.addClass(className);
		},
		onHide: function(self) {
			body.removeClass(className);
		}
	});
}

// mobile menu init
function initMobileNav() {
	ResponsiveHelper.addRange({
		'..1023': {
			on: function() {
				jQuery('body').mobileNav({
					menuActiveClass: 'nav-active',
					menuOpener: '.nav-opener'
				});
			},
			off: function() {
				jQuery('body').mobileNav('destroy');
			}
		}
	});
}

// open-close init
function initOpenClose() {
	jQuery('.lang').openClose({
		activeClass: 'active',
		opener: '.opener',
		slider: 'ul',
		animSpeed: 400,
		effect: 'slide'
	});
}

// in view port init
function initInViewport() {
	jQuery('.viewport-section').itemInViewport({
		visibleMode: 3
	});
}

